import React, { useState } from 'react';
import { ChevronLeft, ChevronRight, SlidersHorizontal, ChevronDown } from 'lucide-react';
import './runstyle.css'
const RunningPage = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const carouselTexts = [
    "MOVE, SHOP, CUSTOMISE & CELEBRATE WITH US",
    "NEW ARRIVALS FOR YOUR BEST RUN",
    "MEMBER EXCLUSIVE DEALS"
  ];

  const sidebarCategories = [
    "Shoes",
    "Tops & T-Shirts",
    "Hoodies & Sweatshirts",
    "Jackets",
    "Trousers & Tights",
    "Shorts",
    "Tracksuits",
    "Socks",
    "Accessories & Equipment"
  ];

  const products = [
    {
      id: 1,
      name: "Nike Running Division",
      description: "Women's Running Jacket",
      price: "₹ 8,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/api/placeholder/400/500"
    },
    {
      id: 2,
      name: "Nike Trail",
      description: "Men's Dri-FIT ADV UV Running Shirt Jacket",
      price: "₹ 5,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/api/placeholder/400/500"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % carouselTexts.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + carouselTexts.length) % carouselTexts.length);
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Top Carousel */}
      <div className="relative bg-gray-100 py-3">
        <button 
          onClick={prevSlide}
          className="absolute left-4 top-1/2 transform -translate-y-1/2"
        >
          <ChevronLeft className="w-6 h-6" />
        </button>
        
        <div className="text-center">
          <p className="text-sm font-medium">
            {carouselTexts[currentSlide]}
          </p>
          {currentSlide === 0 && (
            <div className="mt-1">
              <span className="text-sm">No matter what you feel like doing today, It's better as a Member.</span>
              <a href="#" className="ml-2 text-sm underline font-medium">Join Us</a>
            </div>
          )}
        </div>

        <button 
          onClick={nextSlide}
          className="absolute right-4 top-1/2 transform -translate-y-1/2"
        >
          <ChevronRight className="w-6 h-6" />
        </button>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold">Running (604)</h1>
          <div className="flex gap-4">
            <button className="flex items-center gap-2">
              Hide Filters <SlidersHorizontal className="w-5 h-5" />
            </button>
            <button className="flex items-center gap-2">
              Sort By <ChevronDown className="w-5 h-5" />
            </button>
          </div>
        </div>

        <div className="flex gap-8">
          {/* Sidebar */}
          <div className="w-64 flex-shrink-0">
            <nav className="space-y-4">
              {sidebarCategories.map((category) => (
                <a
                  key={category}
                  href="#"
                  className="block text-gray-600 hover:text-black"
                >
                  {category}
                </a>
              ))}
            </nav>

            <div className="mt-8 space-y-4">
              <button className="flex items-center justify-between w-full">
                Gender <ChevronDown className="w-5 h-5" />
              </button>
              <button className="flex items-center justify-between w-full">
                Kids <ChevronDown className="w-5 h-5" />
              </button>
              <button className="flex items-center justify-between w-full">
                Shop By Price <ChevronDown className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* Product Grid */}
          <div className="flex-1 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Featured Pegasus Banner */}
            <div className="md:col-span-2 lg:col-span-3 relative bg-gray-900 rounded-lg overflow-hidden">
              <img 
                src="/api/placeholder/1200/600" 
                alt="Run In Pegasus 41" 
                className="w-full object-cover"
              />
              <div className="absolute bottom-8 left-8 text-white">
                <h2 className="text-2xl font-bold mb-4">Run In Pegasus 41</h2>
                <button className="bg-white text-black px-6 py-2 rounded-full">
                  Shop
                </button>
              </div>
            </div>

            {/* Product Cards */}
            {products.map((product) => (
              <div key={product.id} className="group">
                <div className="relative">
                  <img
                    src={product.imagePath}
                    alt={product.name}
                    className="w-full object-cover rounded-lg"
                  />
                  {product.tag && (
                    <span className="absolute top-4 left-4 text-sm">
                      {product.tag}
                    </span>
                  )}
                </div>
                <div className="mt-4">
                  <h3 className="font-medium">{product.name}</h3>
                  <p className="text-gray-600">{product.description}</p>
                  <p className="text-gray-500 mt-1">{product.colors}</p>
                  <p className="font-medium mt-1">{product.price}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RunningPage;
