import React, { useState, useEffect } from 'react';
import './runstyle.css';

const Running = () => {
  const [currentText, setCurrentText] = useState("Home");

  const navTexts = [
    "MOVE, SHOP, CUSTOMISE & CELEBRATE WITH US",
    "NEW ARRIVALS FOR YOUR BEST RUN",
    "MEMBER EXCLUSIVE DEALS"
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentText((prevText) => {
        const currentIndex = navTexts.indexOf(prevText);
        const nextIndex = (currentIndex + 1) % navTexts.length;
        return navTexts[nextIndex];
      });
    }, 1000);

    return () => clearInterval(intervalId); // Clean up the interval on component unmount
  }, []);

  // Products Data
  const products = [
    {
      id: 1,
      name: "Nike Running Division",
      description: "Women's Running Jacket",
      price: "₹ 8,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/public/media\rn1.png"
    },
    {
      id: 2,
      name: "Nike Trail",
      description: "Men's Dri-FIT ADV UV Running Shirt Jacket",
      price: "₹ 5,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/media/rn2.png"
    }
  ];

  return (
    <div>
      {/* Navbar Section */}
      <nav className="navbar1">
        <ul>
          <li>{currentText}</li>
        </ul>
      </nav>

      {/* Product Grid Section */}
      <div className="product-grid">
        {/* Featured Pegasus Banner */}
        <div className="featured-banner">
          <img src="/api/placeholder/1200/600" alt="Run In Pegasus 41" className="banner-image" />
          <div className="banner-content">
            <h2 className="banner-title">Run In Pegasus 41</h2>
            <button className="shop-btn">Shop</button>
          </div>
        </div>

        {/* Product Cards */}
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <div className="product-img-container">
              <img src={product.imagePath} alt={product.name} className="product-img" />
              {product.tag && <span className="product-tag">{product.tag}</span>}
            </div>
            <div className="product-info">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-description">{product.description}</p>
              <p className="product-colors">{product.colors}</p>
              <p className="product-price">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Running;
