import React, { useState } from 'react';
import { ChevronLeft, ChevronRight, SlidersHorizontal, ChevronDown } from 'lucide-react';
import './runstyle.css';

const RunningPage = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [activeCategory, setActiveCategory] = useState(null);

  const carouselTexts = [
    "MOVE, SHOP, CUSTOMISE & CELEBRATE WITH US",
    "NEW ARRIVALS FOR YOUR BEST RUN",
    "MEMBER EXCLUSIVE DEALS"
  ];

  const sidebarCategories = [
    "Shoes",
    "Tops & T-Shirts",
    "Hoodies & Sweatshirts",
    "Jackets",
    "Trousers & Tights",
    "Shorts",
    "Tracksuits",
    "Socks",
    "Accessories & Equipment"
  ];

  const products = [
    {
      id: 1,
      name: "Nike Running Division",
      description: "Women's Running Jacket",
      price: "₹ 8,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/media/rn1.png"
    },
    {
      id: 2,
      name: "Nike Trail",
      description: "Men's Dri-FIT ADV UV Running Shirt Jacket",
      price: "₹ 5,995.00",
      colors: "1 Colour",
      tag: "Just In",
      imagePath: "/media/rn2.png"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % carouselTexts.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + carouselTexts.length) % carouselTexts.length);
  };

  const handleCategoryClick = (category) => {
    setActiveCategory((prevCategory) =>
      prevCategory === category ? null : category
    );
  };

  return (
    <div className="running-page">
      {/* Top Carousel */}
      <div className="carousel-container">
        <button onClick={prevSlide} className="carousel-btn prev">
          <ChevronLeft className="carousel-icon" />
        </button>
        
        <div className="carousel-content">
          <p className="carousel-text">
            {carouselTexts[currentSlide]}
          </p>
          {currentSlide === 0 && (
            <div className="carousel-subtext">
              <span>No matter what you feel like doing today, It's better as a Member.</span>
              <a href="#" className="join-link">Join Us</a>
            </div>
          )}
        </div>

        <button onClick={nextSlide} className="carousel-btn next">
          <ChevronRight className="carousel-icon" />
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="page-header">
          <h1 className="page-title">Running (604)</h1>
          <div className="filter-controls">
            <button className="filter-toggle">
              Hide Filters <SlidersHorizontal className="icon-size" />
            </button>
            <button className="filter-sort">
              Sort By <ChevronDown className="icon-size" />
            </button>
          </div>
        </div>

        <div className="content-wrapper">
          {/* Sidebar */}
          <div className="sidebar">
            <nav className="category-nav">
              {sidebarCategories.map((category) => (
                <div key={category} className="category-item">
                  <button
                    className="category-link"
                    onClick={() => handleCategoryClick(category)}
                  >
                    {category} <ChevronDown className="icon-size" />
                  </button>
                  {activeCategory === category && (
                    <div className="dropdown-menu">
                      {/* Dropdown content for the selected category */}
                      <button className="dropdown-item">Option 1</button>
                      <button className="dropdown-item">Option 2</button>
                      <button className="dropdown-item">Option 3</button>
                    </div>
                  )}
                </div>
              ))}
            </nav>
          </div>

          {/* Product Grid */}
          <div className="product-grid">
            {/* Featured Pegasus Banner */}
            <div className="featured-banner">
              <img src="/api/placeholder/1200/600" alt="Run In Pegasus 41" className="banner-image" />
              <div className="banner-content">
                <h2 className="banner-title">Run In Pegasus 41</h2>
                <button className="shop-btn">Shop</button>
              </div>
            </div>

            {/* Product Cards */}
            {products.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-img-container">
                  <img src={product.imagePath} alt={product.name} className="product-img" />
                  {product.tag && <span className="product-tag">{product.tag}</span>}
                </div>
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-description">{product.description}</p>
                  <p className="product-colors">{product.colors}</p>
                  <p className="product-price">{product.price}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RunningPage;
