import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './loginstyle.css';

const NikeSignInPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false); // For tracking if the request is in progress

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Reset error message on form submission
    setLoading(true); // Set loading state to true

    try {
      // Send POST request to signin endpoint
      const response = await axios.post('http://localhost:5000/api/users/signin', {
        email,
        password,
      });

      // On successful login, store JWT token and redirect
      if (response.data.token) {
        localStorage.setItem('token', response.data.token); // Save token in localStorage
        navigate('/'); // Redirect to the home page or app.jsx
      }
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred during login');
    } finally {
      setLoading(false); // Set loading state to false after the request completes
    }
  };

  return (
    <div className="nikeLoginContainer">
      <div className="nikePageWrapper">
        {/* Slogan Section */}
        <div className="nikeSloganContainer">
          <img
            className="nikeSloganImage"
            src="./media/jdt.png" 
            alt="Nike Slogan"
          />
        </div>

        {/* Login Form Section */}
        <div className="nikeSignInCard">
          <div className="nikeLogoContainer">
            <img className="nikeLogo" src="/media/logo1.png" alt="Nike Logo" />
          </div>

          <form onSubmit={handleSubmit} className="nikeForm">
            <h2 className="nikeSignInTitle">IT'S BETTER AS<br />A MEMBER</h2>

            {error && <div className="nikeErrorMessage">{error}</div>} {/* Display error messages */}

            <div className="nikeInputGroup">
              <input
                type="email"
                placeholder="Email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="nikeInputField"
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="nikeInputField"
                required
              />
            </div>

            <div className="nikeRememberForgot">
              <label className="nikeCheckbox">
                <input type="checkbox" className="nikeCheckboxInput" />
                Keep me signed in
              </label>
              <a href="#" className="nikeForgotPassword">Forgot password?</a>
            </div>

            <button type="submit" className="nikeSubmitButton" disabled={loading}>
              {loading ? 'Signing In...' : 'SIGN IN'}
            </button> {/* Disable button while loading */}

            <div className="nikeSignUpText">
              Not a Member?{' '}
              <Link to="/signup" className="nikeSignUpLink">Join Us</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NikeSignInPage;
