import React, { useState, useEffect, useRef } from 'react';
import './style.css';

const App = () => {
  // States
  const [loadingComplete, setLoadingComplete] = useState(false);
  const [showSearchBar, setShowSearchBar] = useState(false);
  const scrollContainerRef = useRef(null);

  // Effects
  useEffect(() => {
    const timer = setTimeout(() => setLoadingComplete(true), 2000);
    return () => clearTimeout(timer);
  }, []);

  // Handlers
  const toggleSearchBar = () => setShowSearchBar(!showSearchBar);

  // Helper Function for Rendering Dropdown
  const renderDropdown = (title, categories) => (
    <div className="dropdown">
      <div className="sub-dropdown">
        {categories.map((category, index) => (
          <div className="category-section" key={index}>
            <h3 className="category-title">{category.title}</h3>
            {category.items.map((item, subIndex) => (
              <div className="sub-dropdown-row" key={subIndex}>
                {item}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );

  // Menu Data
  const menuItems = [
    {
      title: 'Men',
      categories: [
        { title: 'Shoes', items: ['New Releases', 'Running', 'Basketball', 'Football', 'Training & Gym', 'Lifestyle'] },
        { title: 'Clothing', items: ['T-Shirts & Tops', 'Hoodies & Sweatshirts', 'Jackets', 'Shorts', 'Tracksuits'] },
        { title: 'Accessories', items: ['Bags & Backpacks', 'Socks', 'Hats & Headwear'] },
      ],
    },
    // More categories...
  ];

  // Image Data
  const imagesData = [
    { src: 'media/shoe1.jpg', name: 'AIR JORDAN 1', price: '$120' },
    { src: 'media/shoe2.png', name: 'AIR FORCE 1', price: '$100' },
    // More images...
  ];

  const elegantCarouselImages = [
    'media/shoe1.jpg',
    'media/shoe2.png',
    'media/sh3.png',
    // More images...
  ];

  // Component Return
  return (
    <div>
      {/* Loading Screen */}
      {!loadingComplete ? (
        <div className="loading-screen">
          <img src="media/logo.png" alt="Nike Logo" className="nike-logo" />
        </div>
      ) : (
        <div className="main-page">
          {/* Navbar */}
          <div className="navbar">
            <div className="logo-container">
              <img src="media/logo1.png" alt="Nike Logo" className="nike-logo-navbar" />
              <div className="features">
                {menuItems.map((menu, index) => (
                  <span className="feature" key={index}>
                    {menu.title}
                    {renderDropdown(menu.title, menu.categories)}
                  </span>
                ))}
              </div>
            </div>
            <div className="right-icons">
              <div className="search-container">
                <img src="media/search-icon.png" className="icon search-icon" alt="Search" onClick={toggleSearchBar} />
                <input
                  type="text"
                  className={`search-bar ${showSearchBar ? 'show' : ''}`}
                  placeholder="Search..."
                />
              </div>
              <img src="media/user-icon.png" className="icon" alt="User" />
              <img src="media/menu-icon.png" className="icon" alt="Menu" />
            </div>
          </div>

          {/* Content Section */}
          <div className="content">
            <div className="video-container">
              <video
                className="responsive-video"
                src="media/videoplayback.mp4"
                alt="Promotional Video"
                muted
                autoPlay
                loop
              ></video>
            </div>
            <div className="content-details">
              <h4>Nike Air</h4>
              <h4 className="title-container">
                Get Comfortable With <span className="winning">Winning</span>
              </h4>
              <h2>Comfort you want with support you need to power your wins.</h2>
              <button className="shop-button">Shop Now</button>
            </div>
          </div>

          {/* Horizontal Scrolling Images */}
          <div className="image-carousel">
            <h2>New Arrivals</h2>
            <div className="scrolling-images">
              {imagesData.map((image, index) => (
                <div className="image-card" key={index}>
                  <img src={image.src} alt={image.name} className="carousel-image" />
                  <div className="image-details">
                    <h3>{image.name}</h3>
                    <p>{image.price}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Featured Collection */}
          <div className="elegant-carousel-section">
            <h2>Featured Collection</h2>
            <div className="elegant-carousel">
              <div className="elegant-scroll-container" ref={scrollContainerRef}>
                <div className="elegant-scroll-wrapper">
                  {elegantCarouselImages.map((src, index) => (
                    <div key={index} className="elegant-image-card">
                      <img src={src} alt={`Collection ${index + 1}`} className="elegant-image" />
                      <button className="elegant-overlay-button">Shop Now</button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
