import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './loginstyle.css';

const NikeSignInPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error when user starts typing
    setError('');
  };

  const validateForm = () => {
    // Email validation
    if (!formData.email) {
      setError('Please enter your email address');
      return false;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('Please enter a valid email address');
      return false;
    }

    // Password validation
    if (!formData.password) {
      setError('Please enter your password');
      return false;
    }
    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!validateForm()) return;
    
    setIsLoading(true);

    try {
      const apiUrl = `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/users/login`;
      console.log('Attempting login to:', apiUrl);
      console.log('Request payload:', { email: formData.email, password: '***' });

      const response = await axios.post(
        apiUrl,
        {
          email: formData.email.trim().toLowerCase(), // Normalize email
          password: formData.password
        },
        {
          headers: {
            'Content-Type': 'application/json'
          },
          timeout: 5000
        }
      );

      if (response.data.token) {
        // Handle successful login
        if (formData.rememberMe) {
          localStorage.setItem('token', response.data.token);
        } else {
          sessionStorage.setItem('token', response.data.token);
        }

        if (response.data.user) {
          localStorage.setItem('userData', JSON.stringify(response.data.user));
        }

        navigate('/');
      } else {
        setError('Login failed, please check your credentials');
      }
    } catch (error) {
      console.error('Login error details:', {
        status: error.response?.status,
        statusText: error.response?.statusText,
        data: error.response?.data,
        message: error.message
      });

      if (error.code === 'ECONNABORTED') {
        setError('Connection timeout. Please try again.');
      } else if (!error.response) {
        setError('Network error. Please check your connection.');
      } else if (error.response.status === 400) {
        // Handle specific 400 error cases
        const errorMessage = error.response.data?.message;
        if (errorMessage?.toLowerCase().includes('email')) {
          setError('Invalid email format or email not found');
        } else if (errorMessage?.toLowerCase().includes('password')) {
          setError('Invalid password');
        } else {
          setError(errorMessage || 'Invalid login credentials');
        }
      } else if (error.response.status === 429) {
        setError('Too many login attempts. Please try again later.');
      } else {
        setError('An unexpected error occurred. Please try again later.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="nikeLoginContainer">
      <div className="nikePageWrapper">
        <div className="nikeSloganContainer">
          <img
            className="nikeSloganImage"
            src="./media/jdt.png"
            alt="Nike Slogan"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = '/media/fallback-slogan.png';
            }}
          />
        </div>

        <div className="nikeSignInCard">
          <div className="nikeLogoContainer">
            <img 
              className="nikeLogo" 
              src="/media/logo1.png" 
              alt="Nike Logo"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = '/media/fallback-logo.png';
              }}
            />
          </div>

          <form onSubmit={handleSubmit} className="nikeForm" noValidate>
            <h2 className="nikeSignInTitle">
              IT'S BETTER AS<br />A MEMBER
            </h2>

            {error && (
              <div className="nikeErrorMessage" role="alert">
                {error}
              </div>
            )}

            <div className="nikeInputGroup">
              <input
                type="email"
                name="email"
                placeholder="Email address"
                value={formData.email}
                onChange={handleChange}
                className="nikeInputField"
                disabled={isLoading}
                autoComplete="email"
                required
                aria-label="Email address"
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                className="nikeInputField"
                disabled={isLoading}
                autoComplete="current-password"
                required
                aria-label="Password"
                minLength="6"
              />
            </div>

            <div className="nikeRememberForgot">
              <label className="nikeCheckbox">
                <input
                  type="checkbox"
                  name="rememberMe"
                  checked={formData.rememberMe}
                  onChange={handleChange}
                  className="nikeCheckboxInput"
                  disabled={isLoading}
                />
                Keep me signed in
              </label>
              <Link to="/forgot-password" className="nikeForgotPassword">
                Forgot password?
              </Link>
            </div>

            <button
              type="submit"
              className={`nikeSubmitButton ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? 'SIGNING IN...' : 'SIGN IN'}
            </button>

            <div className="nikeSignUpText">
              Not a Member?{' '}
              <Link to="/signup" className="nikeSignUpLink">
                Join Us
              </Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NikeSignInPage;
