import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './signupstyle.css';

const NikeSignUpPage = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false); // To manage the loading state

  // Validate email format using a regex
  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Reset error message on form submission
    setLoading(true); // Start loading state

    // Basic form validation
    if (!validateEmail(email)) {
      setError('Please enter a valid email address');
      setLoading(false); // Stop loading state on error
      return;
    }

    if (password.length < 6) {
      setError('Password must be at least 6 characters long');
      setLoading(false); // Stop loading state on error
      return;
    }

    try {
      // Send POST request to signup endpoint
      const response = await axios.post('http://localhost:5000/api/users/signup', {
        name,
        email,
        password,
      });

      // If signup is successful, redirect to the login page
      if (response.status === 201) {
        navigate('/login'); // Redirect to login page
      }
    } catch (error) {
      // Handle error response
      setError(error.response?.data?.message || 'An error occurred during signup');
    } finally {
      setLoading(false); // Stop loading state after request
    }
  };

  return (
    <div className="nikeSignUpContainer">
      <div className="nikePageWrapper">
        {/* Slogan Section */}
        <div className="nikeSloganContainer">
          <img
            className="nikeSloganImage"
            src="./media/jdt.png" 
            alt="Nike Slogan"
          />
        </div>

        {/* Sign Up Form Section */}
        <div className="nikeSignUpCard">
          <div className="nikeLogoContainer">
            <img className="nikeLogo" src="/media/logo1.png" alt="Nike Logo" />
          </div>

          <form onSubmit={handleSubmit} className="nikeForm">
            <h2 className="nikeSignUpTitle">CREATE YOUR ACCOUNT</h2>

            {error && <div className="nikeErrorMessage">{error}</div>} {/* Display error messages */}

            <div className="nikeInputGroup">
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="nikeInputField"
                required
              />
              <input
                type="email"
                placeholder="Email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="nikeInputField"
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="nikeInputField"
                required
              />
            </div>

            <div className="nikeRememberForgot">
              <label className="nikeCheckbox">
                <input type="checkbox" className="nikeCheckboxInput" />
                Keep me signed in
              </label>
            </div>

            <button type="submit" className="nikeSubmitButton" disabled={loading}>
              {loading ? 'Signing Up...' : 'SIGN UP'} {/* Show loading text when submitting */}
            </button>

            <div className="nikeSignInText">
              Already a Member?{' '}
              <Link to="/login" className="nikeSignUpLink">Sign In</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NikeSignUpPage;
