import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [loadingComplete, setLoadingComplete] = useState(false);
  const [showSignIn, setShowSignIn] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoadingComplete(true);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  const toggleSignIn = () => setShowSignIn(!showSignIn);
  const toggleSignUp = () => setShowSignUp(!showSignUp);

  return (
    <div>
      {!loadingComplete ? (
        <div className="loading-screen">
          <img src="media/logo.png" alt="Nike Logo" className="nike-logo" />
        </div>
      ) : (
        <div className="main-page">
          <Navbar toggleSignIn={toggleSignIn} toggleSignUp={toggleSignUp} />
          {showSignIn ? (
            <SignInForm toggleSignIn={toggleSignIn} />
          ) : showSignUp ? (
            <SignUpForm toggleSignUp={toggleSignUp} />
          ) : (
            <div>
              <MainPage />
              <ImageCarousel />
              <NewSection />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const Navbar = ({ toggleSignIn, toggleSignUp }) => {
  const [showSearchBar, setShowSearchBar] = useState(false);

  const toggleSearchBar = () => {
    setShowSearchBar(!showSearchBar);
  };

  return (
    <div className="navbar">
      <div className="logo-container">
        <img src="media/logo1.png" alt="Nike Logo" className="nike-logo-navbar" />
        <div className="features">
          <span className="feature">Men</span>
          <span className="feature">Women</span>
          <span className="feature">Kids</span>
          <span className="feature">Sale</span>
        </div>
      </div>
      <div className="right-icons">
        <div className="search-container">
          <img
            src="media/search-icon.png"
            className="icon search-icon"
            alt="Search"
            onClick={toggleSearchBar}
          />
          <input
            type="text"
            className={`search-bar ${showSearchBar ? 'show' : ''}`}
            placeholder="Search..."
          />
        </div>
        <img src="media/user-icon.png" className="icon" alt="User" onClick={toggleSignIn} />
        <img src="media/menu-icon.png" className="icon" alt="Menu" onClick={toggleSignUp} />
      </div>
    </div>
  );
};

const SignInForm = ({ toggleSignIn }) => {
  return (
    <div className="auth-form">
      <h2>Sign In</h2>
      <input type="email" placeholder="Email" className="auth-input" />
      <input type="password" placeholder="Password" className="auth-input" />
      <button className="auth-button" onClick={toggleSignIn}>Sign In</button>
      <p>Don't have an account? <span className="link" onClick={toggleSignIn}>Sign Up</span></p>
    </div>
  );
};

const SignUpForm = ({ toggleSignUp }) => {
  return (
    <div className="auth-form">
      <h2>Sign Up</h2>
      <input type="email" placeholder="Email" className="auth-input" />
      <input type="password" placeholder="Password" className="auth-input" />
      <input type="password" placeholder="Confirm Password" className="auth-input" />
      <button className="auth-button" onClick={toggleSignUp}>Sign Up</button>
      <p>Already have an account? <span className="link" onClick={toggleSignUp}>Sign In</span></p>
    </div>
  );
};

const MainPage = () => {
  return (
    <div className="content">
      <div className="video-container">
        <video
          className="responsive-video"
          src="media/videoplayback.mp4"
          alt="Promotional Video"
          muted
          autoPlay
          loop
        ></video>
      </div>
      <div className="content-details">
        <h4>Nike Air</h4>
        <h4 className="title-container">
          Get Comfortable With <span className="winning">Winning</span>
        </h4>
        <h2>Comfort you want with support you need to power your wins.</h2>
        <button className="shop-button">Shop Now</button>
      </div>
    </div>
  );
};

const ImageCarousel = () => {
  const imagesData = [
    { src: 'media/shoe1.jpg', name: 'AIR JORDAN 1', price: '$120' },
    { src: 'media/shoe2.png', name: 'AIR FORCE 1', price: '$100' },
    { src: 'media/sh3.png', name: 'DUNKS', price: '$150' },
    { src: 'media/sh4.png', name: 'VAPORFLY', price: '$90' },
    { src: 'media/sh5.jpg', name: 'VOMERO', price: '$130' },
    { src: 'media/sh6.jpg', name: 'KILLSHOT', price: '$110' },
  ];

  return (
    <div className="image-carousel">
      <h2>New Arrivals</h2>
      <div className="scrolling-images">
        {imagesData.map((image, index) => (
          <div className="image-card" key={index}>
            <img src={image.src} alt={image.name} className="carousel-image" />
            <div className="image-details">
              <h3>{image.name}</h3>
              <p>{image.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const NewSection = () => {
  return (
    <div className="newsection">
      <div className="header">
        <h2>Don't Miss</h2>
      </div>
      <div className="image-container">
        <img src="media/grd.jpg" className="image" alt="Don't Miss" />
      </div>
      <div className="below-grid">
        <h4 className="heading1">Men's Air Jordan 4RM</h4>
        <h4 className="heading2">RIDE EASY</h4>
        <h4 className="heading3">
          A new take on the iconic style, the 4RM puts bold colour blocking in a comfortable low profile.
        </h4>
      </div>
    </div>
  );
};

export default App;
